<script>
    document.addEventListener("DOMContentLoaded", function() {
        const clubLinks = document.querySelectorAll(".club-link");
        const clubDetails = document.querySelectorAll(".club-detail");

        clubLinks.forEach(link => {
            link.addEventListener("click", function() {
                const targetClub = this.getAttribute("data-club");

                clubDetails.forEach(detail => {
                    detail.style.display = detail.id === targetClub ? "block" : "none";
                });

                clubLinks.forEach(link => {
                    link.classList.remove("active");
                });

                this.classList.add("active");
            });
        });

        // Display the first club detail and mark the first club link as active by default
        clubDetails[0].style.display = "block";
        clubLinks[0].classList.add("active");
    });
</script>

